const filtrarGeneral=()=>{const term=document.getElementById("searchGeneral").value.toLowerCase().trim();const words=term?term.split(/\s+/):[];const resultEl=document.getElementById("resultadoBusqueda");let matchCount=0;document.querySelectorAll(".accordion-flush").forEach((item)=>{const btn=item.querySelector(".accordion-button");const collapseEl=item.querySelector(".collapse");const bs=bootstrap.Collapse.getOrCreateInstance(collapseEl,{toggle:!1});if(!term){item.style.display="";bs.hide();btn.classList.add("collapsed");btn.setAttribute("aria-expanded","false");item.querySelectorAll("tbody tr").forEach((row)=>{row.style.display=""});return}
const headerText=btn.innerText.toLowerCase();const rows=Array.from(item.querySelectorAll("tbody tr"));let itemHasMatch=!1;rows.forEach((row)=>{const raw=Array.from(row.cells).filter((_,i)=>i!==2).map((td)=>td.innerText).join(" ").toLowerCase()+" "+headerText;if(!row.innerText.trim()){row.style.display="none"}else{const isMatch=words.every((w)=>raw.includes(w));row.style.display=isMatch?"":"none";if(isMatch){itemHasMatch=!0;matchCount++}}});if(itemHasMatch){item.style.display="";bs.show();btn.classList.remove("collapsed");btn.setAttribute("aria-expanded","true")}else{bs.hide();btn.classList.add("collapsed");btn.setAttribute("aria-expanded","false");item.style.display="none"}});if(!term){resultEl.textContent="";resultEl.classList.remove("no-resultado","resultado-encontrado")}else if(matchCount===0){resultEl.textContent="Sin resultados. Consultar existencia por WhatsApp";resultEl.classList.add("no-resultado");resultEl.classList.remove("resultado-encontrado")}else{resultEl.textContent=`${matchCount} resultado${matchCount !== 1 ? "s" : ""} encontrado${matchCount !== 1 ? "s" : ""}`;resultEl.classList.add("resultado-encontrado");resultEl.classList.remove("no-resultado")}};function debounce(fn,wait){let timeout;return(...args)=>{clearTimeout(timeout);timeout=setTimeout(()=>fn.apply(this,args),wait)}}
